<script>
import { mapActions, mapState } from 'vuex'
import { gsap } from 'gsap'
import { ScrollTrigger } from 'gsap/ScrollTrigger'

export default {
  name: 'IndexPage',
  async asyncData ({ $config, $axios }) {
    const news = await $axios.$get(`${$config.baseURL}/wp-json/wp/api/news`)
    return {
      news
    }
  },
  data () {
    return {
      kvFlag: false
    }
  },
  computed: {
    ...mapState('mouseMove', ['isActive']),
    newsData () {
      return this.news.data.filter((item, index) => {
        if (index >= 0 && index < 3) return item
        return false
      })
    }
  },
  created () {
    if (process.client) { gsap.registerPlugin(ScrollTrigger) }
  },
  mounted () {
    window.addEventListener('load resize', this.HoverImg())
    this.kvFlag = true
    const lContents = document.getElementById('l_contents')
    console.log(lContents)
    setTimeout(() => {
      lContents.classList.add('active')
    }, 7000)
    this.DobrandTtlAnim()
    this.DonewsTtlAnimSec()
    this.DocontactTtlAnimThi()
    this.DoUnderbarAnim()
    this.DoUnderbarAnimSec()
    this.observeMouse()
  },
  methods: {
    ...mapActions('mouseMove', ['updateIsActive']),
    DobrandTtlAnim () {
      gsap.to('.bar_01', { // 
        scrollTrigger: {
          trigger: '.bar_01', // 
          start: 'top 90%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
      gsap.to('.ttl_txt_01', { // 
        scrollTrigger: {
          trigger: '.ttl_txt_01', // 
          start: 'top 90%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
    },
    DonewsTtlAnimSec () {
      gsap.to('.bar_02', { // 
        scrollTrigger: {
          trigger: '.bar_02', // 
          start: 'top 90%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
      gsap.to('.ttl_txt_02', { // 
        scrollTrigger: {
          trigger: '.ttl_txt_02', // 
          start: 'top 90%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
    },
    DocontactTtlAnimThi () {
      gsap.to('.bar_03', { // 
        scrollTrigger: {
          trigger: '.bar_03', // 
          start: 'top 90%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
      gsap.to('.ttl_txt_03', { // 
        scrollTrigger: {
          trigger: '.ttl_txt_03', // 
          start: 'top 90%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
    },
    HoverImg () {
      if (window.matchMedia('(min-width:820px)').matches) {
        const hoverBox = document.querySelectorAll('.hoverBox')
        const hoverImg = document.querySelectorAll('.hoverImg')
        for (let i = 0; i < hoverBox.length; i++) {
          hoverBox[i].addEventListener('mousemove', (e) => {
            const clientRect = hoverBox[i].getBoundingClientRect()
            const top = clientRect.top
            const right = clientRect.right
            const left = clientRect.left
            const X = e.clientX
            const Y = e.clientY
            hoverImg[i].classList.add('active')
            hoverImg[i].style.right = (-X + right - left) * 0.2 + 'px'
            hoverImg[i].style.top = (Y - top - 100) * 0.5 + 'px'
            hoverImg[i].style.transition = '0s'
            hoverImg[i].style.transition = 'opacity 1s, visibility 1s'
          })
          hoverBox[i].addEventListener('mouseleave', () => {
            hoverImg[i].classList.remove('active')
            hoverImg[i].style.transition = '1s'
          })
        }
      }
    },
    DoUnderbarAnim () {
      gsap.to('.btn_01', { // 
        scrollTrigger: {
          trigger: '.btn_01', // 
          start: 'top 80%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
    },
    DoUnderbarAnimSec () {
      gsap.to('.btn_02', { // 
        scrollTrigger: {
          trigger: '.btn_02', // 
          start: 'top 80%', // 
          onToggle: (e) => {
            if (e.isActive) {
              e.trigger.classList.add('active')
            }
          }
        }
      })
    },
    onMouseMove () {
      this.updateIsActive(true)
    },
    onMouseLeave () {
      this.updateIsActive(false)
    },
    observeMouse () {
      const cursor = document.getElementById('cursor')
      const follower = document.getElementById('follower')
      const pos = { x: 0, y: 0 } // 
      const mouse = { x: pos.x, y: pos.y } // 
      const speed = 0.5 // 0.011 
      // 
      const cursorSetX = gsap.quickSetter(cursor, 'x', 'px')
      const cursorSetY = gsap.quickSetter(cursor, 'y', 'px')
      // 
      const followerSetX = gsap.quickSetter(follower, 'x', 'px')
      const followerSetY = gsap.quickSetter(follower, 'y', 'px')
      // 
      document.addEventListener('mousemove', function (event) {
        mouse.x = event.pageX
        mouse.y = event.pageY
      })
      gsap.ticker.add(function () {
        const dt = 1.0 - Math.pow(1.0 - speed, gsap.ticker.deltaRatio())
        pos.x += (mouse.x - pos.x) * dt
        pos.y += (mouse.y - pos.y) * dt
        cursorSetX(pos.x)
        cursorSetY(pos.y)
        followerSetX(pos.x)
        followerSetY(pos.y)
      })
    }
  }
}
